import React, { Component } from "react";
import Airtable from "airtable";
import { NavLink } from "react-router-dom";
import './style.css'

const base = new Airtable({ apiKey: "keyHHrGcNLiewGh05" }).base(
  "appnnQqeeXZmvVJvB"
);

export default class Tabs extends Component {
  constructor(props) {
    super(props);
    this.state = { employee: [] };
  }
  componentDidMount() {
    this.name = this.props.name.match.params.name;
    let a = `'${this.name}'`;
    console.log(a);
    base("Imported table")
      .select({
        view: "Grid view",
        filterByFormula: "SEARCH(" + a + ",{Participant Identifier})",
      })
      .eachPage((records, fetchNextPage) => {
        this.setState({ employee: records });
        fetchNextPage();
      });
  }
  render() {
    console.log(this.state.employee,"records");
    var pName=""
    if(this.state.employee.length!=0)
    {pName=this.state.employee[0].fields['Participant Name']}
    //   this.state.employee.map(value=>
    //      <Data organizerEmail={value.fields["Organizer Email"]} Date={value.fields["Date"]})
    return (
        <div className="rightbar">
          <h1>{pName}</h1>
        <table className="table">
          <thead className="thead-dark">
            <tr>
              <th scope="row">Meeting Code</th>
              <th>Conference ID</th>
              <th scope="row">Event Name</th>
              <th scope="row">Organizer Email</th>
              <th>Meeting Details</th>
            </tr>
          </thead>
          <tbody>
            {this.state.employee.map((value) => (
              <tr>
                <td>
                  <NavLink to={`/Conferences/${value.fields["Meeting Code"]}`}>
                    {value.fields["Meeting Code"]}
                  </NavLink>
                </td>
                <td> <NavLink className="link-conference" to={`/Conference/${value.fields["Conference ID"]}`}>
                {value.fields["Conference ID"]}
              </NavLink>
                    </td>
                <td>{value.fields["Event Name"]}</td>
                <td>{value.fields["Organizer Email"]}</td>
                <td>
                  <NavLink to={`/Employee/${this.name}/details/${value.fields["Conference ID"]}`} className="link">
                    View Details
                  </NavLink>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    );
  }
}
