import React, { Component } from "react";
import ShowData from "./ShowData";
import AirTable from './AirTable'
import SideBar from "../../SideBar";
import InfiniteScroll from "react-infinite-scroll-component";
import Airtable from "airtable";
import ScrollTable from "./ScrollTable";
import "./style.css";

const base = new Airtable({ apiKey: "keyHHrGcNLiewGh05" }).base(
  "appnnQqeeXZmvVJvB"
);

export default class Employees extends Component {
  constructor(props) {
    super(props);
    this.state = { searchField: "", employees: [], size: 10,loading:true,f:""};
  }
  // componentDidMount() {
  //   this.fetchData();
  // }

  handleSearch = (e) => {
    this.setState({ searchField: e.target.value });
  };
  // handleNext = () => {
  //   this.setState({ size: this.state.size + 15 });
  //   //this.fetchData();
  // };
  data=(x)=>{
    console.log(x,"cccc")
    this.setState({employees:[...this.state.employees,...x]})
  }
  func=(x)=>{
    this.setState({f:x})
  }
  handleClick=()=>{
    console.log("click")
    this.state.f()
  }
  render() {
    console.log(this.state.employees,"emp")
    const result = [];
    const map = new Map();
    for (const item of this.state.employees) {
      if (!map.has(item.pName)) {
        map.set(item.pName, true);
        result.push({
          pName: item.pName,
          pId: item.pId,
        });
      }
    }
    var filteredData = result
      .filter((val) =>
      val.pName.toLowerCase().includes(this.state.searchField.toLowerCase())
      )
      .sort((a, b) => a.pName.localeCompare(b.pName));
    return (
      <>
      <AirTable type="employees" pageSize={20} view={"Grid-view"} data={this.data} func={this.func}/>

          <div id="scrollableDiv">
             <InfiniteScroll
              dataLength={this.state.employees.length}
              next={this.handleClick}
              hasMore={true}
              loader={<h4>Loading...</h4>}
            >
              <ShowData data={filteredData} />
            </InfiniteScroll> 
         </div>
         </>
    );
  }
}
