import React, { Component } from "react";
import ShowData from "./ShowData";
import SideBar from './SideBar';
import { Route, NavLink } from "react-router-dom";
import Airtable from "airtable";
import "./style.css";

const base = new Airtable({ apiKey: "keyHHrGcNLiewGh05" }).base(
  "appnnQqeeXZmvVJvB"
);

export default class Employees extends Component {
  constructor(props) {
    super(props);
    this.state = { searchField: "", employees: [] };
  }
  componentDidMount() {
    // let a = "Tushar Bansal";
    //let name = `'${a}'`;
    base("Imported table")
      .select({
        view: "Grid view",
        //filterByFormula: "SEARCH(" + name + ",{Participant Name})",
        fields: ["Date", "Participant Name","Participant Identifier"],
      })
      .eachPage((records, fetchNextPage) => {
        let arr = records.map((record) => record.fields["Participant Name"]);
        let arr1 = records.map((record) => record.fields["Participant Identifier"]);
        let b=[...new Set(arr1)]
        console.log(b)
        let a = [...new Set(arr)];
        this.setState({ employees: a });
        fetchNextPage();
      });
  }
  handleSearch = (e) => {
    this.setState({ searchField: e.target.value });
  };
  render() {
    var filteredData = this.state.employees.filter((val) =>
      val.toLowerCase().includes(this.state.searchField.toLowerCase())
    ).sort();
    return (
      <div className="rightbar">
        <div >
        <nav className="navbar navbar-dark bg-dark">
        <input
          style={{width:"30%"}}
          className="form-control"
          type="search"
          value={this.state.searchField}
          onChange={this.handleSearch}
          placeholder="search here"
        />
        </nav>
        <div >
          <ShowData data={filteredData} />
        </div>
        </div>
      </div>
    );
  }
}
