import React, { Component } from "react";
import Airtable from "airtable";
import ShowData from "./ShowData";
import "./style.css";
import InfiniteScroll from "react-infinite-scroll-component";

const base = new Airtable({ apiKey: "keyHHrGcNLiewGh05" }).base(
  "appnnQqeeXZmvVJvB"
);
export default class AirTable extends Component {
  constructor() {
    super();
    this.state = { data: [], nextList: "", search: ""};
  }
  componentDidMount() {
    this.fetchData();
  }
  componentDidUpdate() {
    if (this.props.search !== this.state.search) {
      this.setState({ search: this.props.search, data: [] });
      this.fetchData();
    }
  }
  fetchData = () => {
    if(this.props.search)
    var pIdentifier = `'${this.props.search}'`;
    else
    pIdentifier=""
    base("Imported table")
      .select({
        view: "Grid view",
        filterByFormula: "SEARCH(" + pIdentifier + ",{Participant Identifier})",
        pageSize: this.props.pageSize,
      })
      .eachPage((records, fetchNextPage) => {
        let array = records.map((record) => ({
          pName: record.fields["Participant Name"],
          pId: record.fields["Participant Identifier"],
        }));
        this.setState({
          data: [...array, ...this.state.data],
          nextList: fetchNextPage,
        });
      });
  };
  handleNext = () => {
    this.state.nextList();
  };


  render() {
    const result = [];
    const map = new Map();
    for (const item of this.state.data) {
      if (!map.has(item.pName)) {
        map.set(item.pName, true);
        result.push({
          pName: item.pName,
          pId: item.pId,
        });
      }
    }
    let filteredData=result.sort((a, b) => a.pName.localeCompare(b.pName))
    return (

      <>
        <div id="scrollableDiv">
          <InfiniteScroll
            dataLength={this.state.data.length}
            next={this.handleNext}
            hasMore={true}
            loader={<h4>Loading...</h4>}
          >
            <ShowData data={filteredData} />
          </InfiniteScroll>
        </div>
      </>
    );
  }
}
