import React, { Component } from "react";
import ShowData from "./ShowData";
import InfiniteScroll from "react-infinite-scroll-component";
import Airtable from "airtable";
import "./style.css";

const base = new Airtable({ apiKey: "keyHHrGcNLiewGh05" }).base(
  "appnnQqeeXZmvVJvB"
);

export default class Employees extends Component {
  constructor(props) {
    super(props);
    this.state = { searchField: "", employees: [], size: 10,loading:true};
    this.container = React.createRef();
  }
  componentDidMount() {
    this.fetchData();
  }
  fetchData = () => {
    base("Imported table")
      .select({
        view: "Grid view",
        pageSize: this.state.size,
      })
      .eachPage((records, fetchNextPage) => {
        let array = records.map((record) => ({
          pName: record.fields["Participant Name"],
          pId: record.fields["Participant Identifier"],
        }));
        let a = [...this.state.employees, ...array];
        const result = [];
        const map = new Map();
        for (const item of a) {
          if (!map.has(item.pName)) {
            map.set(item.pName, true);
            result.push({
              pName: item.pName,
              pId: item.pId,
            });
          }
        }
        console.log(result);
        this.setState({ employees: result });
      });
  };
  shouldComponentUpdate(p,prevs){
    console.log(prevs.employees,"prevs")
    console.log(this.state.employees,"new")
    if(prevs.employees===this.state.employees)
     //{this.setState({loading:false})}
     return true;
  }
  handleSearch = (e) => {
    this.setState({ searchField: e.target.value });
  };
  handleNext = () => {
    this.setState({ size: this.state.size + 15 });
    this.fetchData();
  };
  render() {
    var filteredData = this.state.employees
      .filter((val) =>
        val.pName.toLowerCase().includes(this.state.searchField.toLowerCase())
      )
      .sort((a, b) => a.pName.localeCompare(b.pName));
    return (
      <div className="rightbar">
        <div>
          <input
            style={{ width: "30%" }}
            className="form-control"
            type="search"
            value={this.state.searchField}
            onChange={this.handleSearch}
            placeholder="search here"
          />
          <div id="scrollableDiv">
            <InfiniteScroll
              dataLength={this.state.employees.length}
              next={this.handleNext}
              hasMore={this.state.loading}
              loader={<h4>Loading...</h4>}
            >
              <ShowData data={filteredData} />
            </InfiniteScroll>
          </div>
        </div>
      </div>
    );
  }
}
