import React, { Component } from "react";
import ShowData from "./ShowData";
import AirTable from "./AirTable";
import SideBar from "../../SideBar";
import InfiniteScroll from "react-infinite-scroll-component";
import Airtable from "airtable";
import ScrollTable from "./ScrollTable";
import "./style.css";

const base = new Airtable({ apiKey: "keyHHrGcNLiewGh05" }).base(
  "appnnQqeeXZmvVJvB"
);

export default class Employees extends Component {
  constructor(props) {
    super(props);
    this.state = {
      searchField: "",
      employees: [],
      size: 10,
      loading: true,
      f: "",
    };
  }
  // componentDidMount() {
  //   this.fetchData();
  // }

  handleSearch = (e) => {
    this.setState({ searchField: e.target.value });
  };
  // handleNext = () => {
  //   this.setState({ size: this.state.size + 15 });
  //   //this.fetchData();
  // };
  data = (x) => {
    if (x) this.setState({ employees: [...this.state.employees, ...x] });
    else this.setState({ loading: false });
  };
  func = (x) => {
    //console.log(x)
    this.setState({ f: x });
  };
  handleClick = () => {
    console.log(this.state.f(), "count");
  };
  render() {
    // console.log(this.state.employees,"emp")
    console.log(this.state.searchField, "emp");
    return (
      <>
        <div>
          <input
            style={{ width: "50%" }}
            className="form-control"
            type="search"
            value={this.state.searchField}
            onChange={this.handleSearch}
            placeholder="search here"
          />
        </div>

        <AirTable
          type="employees"
          search={this.state.searchField}
          pageSize={20}
          view={"Grid-view"}
        />
      </>
    );
  }
}
