import React, { Component } from "react";
import { Link,NavLink, withRouter } from "react-router-dom";
import Airtable from "airtable";
import "./style.css";

const base = new Airtable({ apiKey: "keyHHrGcNLiewGh05" }).base(
  "appnnQqeeXZmvVJvB"
);

class Conferences extends Component {
  constructor(props) {
    super(props);
    this.state = { conference: [] };
  }
  componentDidMount() {
    // let a = "Tushar Bansal";
    //let name = `'${a}'`;
    base("Imported table")
      .select({
        view: "Grid view",
        //filterByFormula: "SEARCH(" + name + ",{Participant Name})",
        fields: ["Conference ID", "Date"],
      })
      .eachPage((records, fetchNextPage) => {
        let arr1 = records.map((record) => ({
          id: record.fields["Conference ID"],
          date: record.fields["Date"],
        }));
        const result = [];
        const map = new Map();
        for (const item of arr1) {
          if (!map.has(item.id)) {
            map.set(item.id, true); // set any value to Map
            result.push({
              id: item.id,
              date: item.date,
            });
          }
        }
        console.log(result);
        this.setState({ conference: result });
        fetchNextPage();
      });
  }
  handleBack = () => {
    this.props.history.push("/");
  };
  render() {
    return (
      <div>
        <nav className="navbar navbar-dark bg-dark">
        <Link onClick={this.handleBack}>Home</Link>
         </nav>
        {this.state.conference.map((value) => (
          <div className="card">
            <div className="card-body">
              <NavLink to={`/Conference/${value.id}`}>
                Conference ID- {value.id} <b>{value.date}</b>
              </NavLink>
            </div>
          </div>
        ))}
      </div>
    );
  }
}
export default withRouter(Conferences);
