import React, {Component} from "react"
import { NavLink, withRouter } from "react-router-dom";
import Airtable from "airtable";
import './style.css'

const base = new Airtable({ apiKey: "keyHHrGcNLiewGh05" }).base(
  "appnnQqeeXZmvVJvB"
);

 class Conferences extends Component{
    constructor(props){
        super(props);
        this.state={conference:[]}
    }
    componentDidMount() {
        // let a = "Tushar Bansal";
        //let name = `'${a}'`;
        base("Imported table")
          .select({
            view: "Grid view",
            //filterByFormula: "SEARCH(" + name + ",{Participant Name})",
            fields:["Conference ID","Participant Name"]
      })
          .eachPage((records, fetchNextPage) => {
             let arr = records.map((record) => record.fields["Conference ID"]);
             let a = [...new Set(arr)];
            this.setState({ conference: a });
            fetchNextPage();
          });
      }
      handleBack=()=>{
          this.props.history.push('/')
      }
render(){


    return(
        <div>
        <button onClick={this.handleBack}>Go Back</button>
        {this.state.conference.map((value) => (
            <div className="card">
                <div className="card-body">
              <NavLink to={`/Conference/${value}`}>
               Conference ID- {value}
              </NavLink>
              </div>
        </div>))}
        </div>
    )
}
}
export default withRouter(Conferences)
