import React, { Component } from "react";
import "./App.css";
import {NavLink, Route, Switch} from "react-router-dom"
import Airtable from "airtable";
import Employees from "./Employees";
import Conferences from "./Conferences"
import HomePage from "./HomePage";
import Tabs from './Tabs'

const base = new Airtable({ apiKey: "keyHHrGcNLiewGh05" }).base(
  "appnnQqeeXZmvVJvB"
);

class App extends Component {
  constructor(props) {
    super(props);
    this.state = { goals: [] };
  }
  componentDidMount() {

   // let a = "Tushar Bansal";
    //let name = `'${a}'`;
    base("Imported table")
      .select({
        view: "Grid view",
        //filterByFormula: "SEARCH(" + name + ",{Participant Name})",
        fields: ["Date", "Participant Name"],
      })
      .eachPage((records, fetchNextPage) => { 
        let arr=records.map(record=>record.fields["Participant Name"])
        let a=new Set(arr)
        console.log(a)
        fetchNextPage();
      });
  }

  render() {
    return (
      <>
    <Switch>
    <Route exact path="/" render={()=><HomePage />} />
    <Route exact path="/Employees" render={()=><Employees />} />
    <Route exact path="/Conferences" render={()=><Conferences />} />
    <Route
            exact
            path="/Employees/:name"
            render={(props) => <Tabs name={props}/>}
          />
    </Switch>

    </>
    );
  }
}

export default App;
