import React, { Component } from "react";
import Airtable from "airtable";
import ShowData from "./ShowData";
import "./style.css";
import InfiniteScroll from "react-infinite-scroll-component";

const base = new Airtable({ apiKey: "keyHHrGcNLiewGh05" }).base(
  "appnnQqeeXZmvVJvB"
);
export default class AirTable extends Component {
  constructor() {
    super();
    this.state = { data: [], nextList: "",search:"" };
  }
  componentDidMount() {
    this.fetchData();
  }
  componentDidUpdate(){
    console.log("updte")
    console.log(this.props.search)
    if(this.props.search!==this.state.search)
    this.setState({search:this.props.search})
    console.log(this.state.search,"state")
  }
  fetchData = () => {
    //console.log(this.props.search, "prop");
    base("Imported table")
      .select({
        view: "Grid view",
        pageSize: 20,
      })
      .eachPage((records, fetchNextPage) => {
        let array = records.map((record) => ({
          pName: record.fields["Participant Name"],
          pId: record.fields["Participant Identifier"],
        }));
        this.setState({
          data: [...array, ...this.state.data],
          nextList: fetchNextPage,
          arr: array,
        });
      });
  };
  handleClick = () => {
    this.state.nextList();
  };
  handleSearch = (e) => {
    this.setState({ search: e.target.value });
  };

  render() {
    //console.log(this.props.search,"hedrdd")
    const result = [];
    const map = new Map();
    for (const item of this.state.data) {
      if (!map.has(item.pName)) {
        map.set(item.pName, true);
        result.push({
          pName: item.pName,
          pId: item.pId,
        });
      }
    }
    return (
      <>
        <div>
          <input
            style={{ width: "50%" }}
            className="form-control"
            type="search"
            value={this.state.search}
            onChange={this.handleSearch}
            placeholder="search here"
          />
        </div>
        <div id="scrollableDiv">
          <InfiniteScroll
            dataLength={this.state.data.length}
            next={this.handleClick}
            hasMore={true}
            loader={true ? <h4>Loading...</h4> : null}
          >
            <ShowData data={result} />
          </InfiniteScroll>
        </div>
      </>
    );
  }
}
