import React, { Component } from "react";
import Airtable from "airtable";
import ScrollTable from './ScrollTable'
import ShowData from "./ShowData";
import "./style.css";
import InfiniteScroll from "react-infinite-scroll-component";

const base = new Airtable({ apiKey: "keyHHrGcNLiewGh05" }).base(
  "appnnQqeeXZmvVJvB"
);
export default class AirTable extends Component {
constructor(){
  super();
  this.state={data:[],funct:'',arr:[],search:""}
}
componentDidMount(){
  this.fetchData()
}
fetchData = (search) => {
    console.log(this.props.search,"prop")
    base("Imported table")
      .select({
        view: "Grid view",
        //filterByFormula: "SEARCH(" + pIdentifier + ",{Participant Identifier})",
        pageSize: 20,
      })
      .eachPage((records, fetchNextPage) => {
        let array = records.map((record) => ({
          pName: record.fields["Participant Name"],
          pId: record.fields["Participant Identifier"],
        }));
        //console.log(records,"last slot")
        this.setState({data:[...array,...this.state.data],funct:fetchNextPage,arr:array})

  })
}
handleClick=()=>{
  this.state.funct()
}
handleSearch = (e) => {
  this.setState({ search: e.target.value });
};

  render(){ 
    //this.fetchData(this.state.search)
   // console.log(this.state.search,"searchrender")
   //console.log(this.props.search,"air")
    const result = [];
    const map = new Map();
    for (const item of this.state.data) {
      if (!map.has(item.pName)) {
        map.set(item.pName, true);
        result.push({
          pName: item.pName,
          pId: item.pId,
        });
      }
    }
    // var filteredData = result
    //   .filter((val) =>
    //   val.pName.toLowerCase().includes(this.state.searchField.toLowerCase())
    //   )
    //   .sort((a, b) => a.pName.localeCompare(b.pName));
      return (
      <>
           <div>
          <input
            style={{ width: "50%" }}
            className="form-control"
            type="search"
            value={this.state.search}
            onChange={this.handleSearch}
            placeholder="search here"
          />
          </div>
          <div id="scrollableDiv">
             <InfiniteScroll
              dataLength={this.state.data.length}
              next={this.handleClick}
              hasMore={true}
              loader={true?<h4>Loading...</h4>:null}
            >
              <ShowData data={result} />
            </InfiniteScroll> 
         </div>
      </>)
  }
}
