import React, {Component} from "react"
import Airtable from "airtable";
import "./style.css";

const base = new Airtable({ apiKey: "keyHHrGcNLiewGh05" }).base(
  "appnnQqeeXZmvVJvB"
);
export default class AirTable extends Component{
    componentDidMount(){
        this.fetchData()
    }
    fetchData = () => {
        base("Imported table")
          .select({
            view: "Grid view",
            pageSize: this.state.size,
          })
          .eachPage((records, fetchNextPage) => {
            let array = records.map((record) => ({
              pName: record.fields["Participant Name"],
              pId: record.fields["Participant Identifier"],
            }));
            let a = [...this.state.employees, ...array];
            const result = [];
            const map = new Map();
            for (const item of a) {
              if (!map.has(item.pName)) {
                map.set(item.pName, true);
                result.push({
                  pName: item.pName,
                  pId: item.pId,
                });
              }
            }

            this.setState({ employees: result });
          });
      };
}
